mapper.select:
SELECT id, user_id, password, activate, try_count
FROM USER
WHERE user_id = #{userId};

mapper.update:
UPDATE USER
SET try_count = try_count + 1
WHERE id = #{id};

UPDATE USER
SET try_count = 0
WHERE id = #{id};

mapper.select:
SELECT DATEDIFF(dd, password_date, NOW()) as DATEDIFF
FROM USER
WHERE id = #{id};

SELECT EXISTS(
               SELECT *
               FROM USER
               WHERE user_id = #{userId}
           );

SELECT id
FROM USER
WHERE invite_url_code = #{inviteUrlCode};

mapper.update:
UPDATE USER
SET invite_url_code = #{inviteUrlCode}
WHERE id = #{id};
